function mount_vhdx {
    param (
        $vhdx_path,
        $drive_letter,
        [switch]$need_no_key
    )

    do {

        $null = Get-Partition -DriveLetter $drive_letter -erroraction 'silentlycontinue'
        if ($?) {
            Write-Output "Drive letter ${drive_letter} is already in use."
            break
        }

        # 挂载vhdx    
        $mount_res = Mount-DiskImage $vhdx_path -erroraction 'silentlycontinue'
        if (!$?) {
            Write-Output "Mounting $vhdx_path failed."
            break
        }

        # 获取盘符
        $tmp_drive_letter = ($mount_res | Get-Disk | Get-Partition -PartitionNumber 2 | Get-Volume).DriveLetter
        if ($drive_letter -ne $tmp_drive_letter) {
            # 改变盘符
            Get-Partition -DriveLetter $tmp_drive_letter | Set-Partition -NewDriveLetter $drive_letter
        }

        Write-Output "Successfully mounted $vhdx_path on $drive_letter."

        if ($need_no_key) {
            break
        }

        # 获取密钥
        Set-Location -Path $PSScriptRoot
        $recovery_key = (.\ossutil64.exe -c .\.ossutilconfig cat oss://bucket-name/bitlocker$drive_letter.txt | C:\Install\cygwin64\bin\bash.exe --login -c "head -1 | grep -v Error| sed 's/\n//'")
        if ([string]::IsNullOrEmpty($recovery_key)) {
            Write-Output "Empty recovery key."
            break
        }

        # 解密
        manage-bde.exe -unlock ${drive_letter}: -recoverypassword $recovery_key
        break
    } while ($false)

    $sec = 3
    Write-Output "Waiting for $sec seconds."
    Start-Sleep $sec

    # Pause
}

